pragma solidity ^0.5.0;

import './LightpointToken.sol';

contract LightpointMarketplace {
    string public name;
    uint public productCount = 0;
    mapping(uint => Product) public products;
    LightpointToken public tokenContract;

    struct Product {
        uint id;
        string name;
        uint price;
        address payable owner;
        bool purchased;
    }

    event ProductCreated(
        uint id,
        string name,
        uint price,
        address payable owner,
        bool purchased
    );

    event ProductPurchased(
        uint id,
        string name,
        uint price,
        address payable owner,
        bool purchased
    );

    constructor(LightpointToken _tokenContract) public {
        name = "Lightpoint Marketplace";
        tokenContract = _tokenContract;
    }

    function createProduct(string memory _name, uint256 _price) public {
        // Require a name
        require(bytes(_name).length > 0);

        // Require a valid price
        require(_price > 0);

        // Increment product count
        productCount++;

        // create the product
        products[productCount] = Product(productCount, _name, _price, msg.sender, false);
        
        // trigger an event
        emit ProductCreated(productCount, _name, _price, msg.sender, false);
    }

    function purchaseProduct(uint _id) public payable {
        // Fetch the product
        Product memory _product = products[_id];

        // Fetch the owner
        address payable _seller = _product.owner;
        // Make sure the product is valid
        require(_product.id > 0 && _product.id <= productCount);
        require(msg.value >= _product.price);
        require(!_product.purchased);
        require(_seller != msg.sender);
        // Assumes the calling address has approved this contract as spender
        require(tokenContract.allowance(msg.sender, address(this)) >= msg.value);
        // Transfer ownership
        _product.owner = msg.sender;
        // Make it purchased
        _product.purchased = true;
        // Update the product
        products[_id] = _product;
        // Pay the seller by sending them LPT
        require(tokenContract.transferFrom(msg.sender, _seller, msg.value));
        // Trigger an event
        emit ProductPurchased(productCount, _product.name, _product.price, msg.sender, true);
    }
}
